"use strict"

//Send the app html to the client
exports.app = function(server) {server.get("/app+", function (req, res) {
	res.sendfile("app.html");
});}

//Send the splash html to the client
exports.splash = function(server) {server.get("/splash?", function (req, res) {
	res.sendfile("splash.html");
});}

//Send the dashboard html to the client
exports.dashboard = function(server) {server.get("/dash?bo+ard", function (req, res) {
	res.sendfile("dashboard.html");
});}
